from flask import Flask, render_template, request, flashapp = Flask(__name__)app.config['SECRET_KEY'] = 'bk3gi89d883jki4k'menu = [{"name": "Главная", "url": "/"},        {"name": "Добавить соревнование", "url": "contact"},        {"name": "Анализ заездов", "url": "first-app"},]@app.route("/")def index():    return render_template('index.html', menu=menu)@app.route("/about")def about():    return render_template('about.html', title="О сайте", menu=menu)@app.route("/contact", methods=["POST", "GET"])def addPost():    if request.method == 'POST':        f = open('text.txt', 'w')        if len(request.form['name of competition']) > 2:            f.write(request.form['name of competition'] + ';' + request.form['date'] + ';' + request.form['organizer'] + ';' + request.form['place'] + ';' + request.form['number'] + ';' + request.form['type'])            flash('Сообщение отправлено', category='success')        else:            flash('Ошибка отправки', category='error')        f.close()    return render_template('contact.html', title="Обратная связь", menu=menu)@app.errorhandler(404)def pageNotFound(error):    return render_template('page404.html', title="Страница не найдена", menu=menu)if __name__ == "__main__":    app.run(debug=True)